 21→var connectCmd = &cobra.Command{
 22→	Use:   "connect [name]",
 23→	Short: "SSH into sandbox and attach tmux (auto-starts if stopped)",
 24→	Args:  cobra.MaximumNArgs(1),
 25→	RunE: func(cmd *cobra.Command, args []string) error {
 26→		name := resolveSandboxName(args)
 27→		if name == "" {
