 97→func resolveSandboxName(args []string) string {
 98→	if len(args) > 0 {
 99→		return args[0]
100→	}
101→	return ""
102→}
103→
104→func waitForSSH(host string, port int) error {
105→	addr := net.JoinHostPort(host, strconv.Itoa(port))
106→	deadline := time.Now().Add(30 * time.Second)
107→	printed := false
108→
109→	for time.Now().Before(deadline) {
110→		conn, err := net.DialTimeout("tcp", addr, time.Second)
111→		if err == nil {
112→			conn.Close()
113→			if printed {
114→				fmt.Println()
115→			}
116→			return nil
117→		}
118→		if !printed {
119→			fmt.Printf("Waiting for SSH to be ready...")
120→			printed = true
121→		}
122→		fmt.Print(".")
123→		time.Sleep(500 * time.Millisecond)
124→	}
125→
126→	fmt.Println()
127→	return fmt.Errorf("timeout waiting for SSH at %s", addr)
128→}
129→
130→func sshExec(host string, port int, user string, remoteCmd string) error {
131→	sshArgs := []string{
132→		"-p", strconv.Itoa(port),
133→		"-o", "StrictHostKeyChecking=no",
134→		"-o", "UserKnownHostsFile=/dev/null",
135→		"-o", "LogLevel=ERROR",
136→		fmt.Sprintf("%s@%s", user, host),
137→	}
138→	if remoteCmd != "" {
139→		sshArgs = append(sshArgs, "-t", remoteCmd)
140→	}
141→
142→	sshPath, err := exec.LookPath("ssh")
143→	if err != nil {
144→		return fmt.Errorf("ssh not found: %w", err)
145→	}
146→
147→	if os.Getenv("VERBOSE") == "1" {
148→		fmt.Fprintf(os.Stderr, "→ ssh %s\n", shellJoin(sshArgs))
149→	}
150→
151→	proc := &os.ProcAttr{
152→		Files: []*os.File{os.Stdin, os.Stdout, os.Stderr},
153→	}
154→	process, err := os.StartProcess(sshPath, append([]string{"ssh"}, sshArgs...), proc)
155→	if err != nil {
156→		return fmt.Errorf("starting ssh: %w", err)
157→	}
158→
159→	state, err := process.Wait()
160→	if err != nil {
161→		return err
162→	}
163→	if !state.Success() {
164→		os.Exit(state.ExitCode())
165→	}
166→	return nil
167→}
168→
169→func shellJoin(args []string) string {
170→	quoted := make([]string, len(args))
171→	for i, a := range args {
172→		if strings.ContainsAny(a, " \t\n\"'\\") {
173→			quoted[i] = fmt.Sprintf("%q", a)
174→		} else {
175→			quoted[i] = a
176→		}
177→	}
178→	return strings.Join(quoted, " ")
179→}
