 15→
 16→func verbose() bool {
 17→	v := strings.ToLower(os.Getenv("SANDCASTLE_VERBOSE"))
 18→	return v == "1" || v == "true"
 19→}
 20→
 21→func logVerbose(format string, args ...any) {
 22→	if verbose() {
 23→		fmt.Fprintf(os.Stderr, "\033[2m"+format+"\033[0m\n", args...)
 24→	}
 25→}
 26→
 27→type Client struct {
 28→	BaseURL    string
 29→	Token      string
 30→	HTTPClient *http.Client
 31→}
 32→
 33→func newHTTPClient(insecure bool) *http.Client {
 34→	if insecure {
 35→		return &http.Client{
 36→			Transport: &http.Transport{
 37→				TLSClientConfig: &tls.Config{InsecureSkipVerify: true},
 38→			},
 39→		}
 40→	}
 41→	return &http.Client{}
 42→}
 43→
 44→func NewClient() (*Client, error) {
 45→	cfg, err := config.Load()
 46→	if err != nil {
 47→		return nil, err
 48→	}
 49→	srv, err := cfg.CurrentServerConfig()
 50→	if err != nil {
 51→		return nil, err
 52→	}
 53→	logVerbose("server: %s (%s)", cfg.CurrentServer, srv.URL)
 54→	return &Client{
 55→		BaseURL:    srv.URL,
 56→		Token:      srv.Token,
 57→		HTTPClient: newHTTPClient(srv.Insecure),
 58→	}, nil
 59→}
 60→
 61→func NewClientWithToken(baseURL, token string, insecure bool) *Client {
 62→	return &Client{
 63→		BaseURL:    baseURL,
 64→		Token:      token,
 65→		HTTPClient: newHTTPClient(insecure),
 66→	}
 67→}
 68→
 69→func (c *Client) do(method, path string, body any, result any) error {
 70→	var bodyReader io.Reader
 71→	var reqData []byte
 72→	if body != nil {
 73→		var err error
 74→		reqData, err = json.Marshal(body)
 75→		if err != nil {
 76→			return fmt.Errorf("marshaling request: %w", err)
 77→		}
 78→		bodyReader = bytes.NewReader(reqData)
 79→	}
 80→
 81→	logVerbose("→ %s %s%s", method, c.BaseURL, path)
 82→	if len(reqData) > 0 {
 83→		logVerbose("  request: %s", string(reqData))
 84→	}
 85→
 86→	req, err := http.NewRequest(method, c.BaseURL+path, bodyReader)
 87→	if err != nil {
 88→		return fmt.Errorf("creating request: %w", err)
 89→	}
 90→
 91→	req.Header.Set("Content-Type", "application/json")
 92→	if c.Token != "" {
 93→		req.Header.Set("Authorization", "Bearer "+c.Token)
 94→	}
 95→
 96→	resp, err := c.HTTPClient.Do(req)
 97→	if err != nil {
 98→		return fmt.Errorf("request failed: %w", err)
 99→	}
100→	defer resp.Body.Close()
