// Find the Terminal button and its parent form
const btn = document.querySelector('button[type="submit"]');
const allForms = document.querySelectorAll('form');
let result = [];
allForms.forEach((f, i) => {
  result.push(`Form ${i}: action=${f.action} method=${f.method} innerHTML=${f.innerHTML.substring(0, 200)}`);
});
result.join('\n\n');

// Make the terminal POST via fetch to see the actual response
const csrfToken = document.querySelector('meta[name="csrf-token"]')?.content;
fetch('/sandboxes/1/terminal', {
  method: 'POST',
  headers: {
    'X-CSRF-Token': csrfToken,
    'Accept': 'text/html'
  },
  redirect: 'manual',
  credentials: 'same-origin'
}).then(r => {
  return `Status: ${r.status} ${r.statusText}, Type: ${r.type}, Redirected: ${r.redirected}, URL: ${r.url}, Location: ${r.headers.get('location')}`;
}).catch(e => `Error: ${e.message}`);

// Follow the redirect to see the full chain
const csrfToken = document.querySelector('meta[name="csrf-token"]')?.content;
fetch('/sandboxes/1/terminal', {
  method: 'POST',
  headers: {
    'X-CSRF-Token': csrfToken,
    'Accept': 'text/html'
  },
  redirect: 'follow',
  credentials: 'same-origin'
}).then(async r => {
  const text = await r.text();
  return `Final URL: ${r.url}\nStatus: ${r.status}\nBody preview: ${text.substring(0, 500)}`;
}).catch(e => `Error: ${e.message}`);

// Check for flash messages or alerts on the page
const alerts = document.querySelectorAll('[role="alert"], .alert, .notice, .flash, .error, [data-turbo-temporary]');
let result = [];
alerts.forEach(el => result.push(el.textContent.trim()));
// Also check for any hidden elements with error text
const allDivs = document.querySelectorAll('div');
allDivs.forEach(el => {
  if (el.textContent.includes('error') || el.textContent.includes('Error') || el.textContent.includes('failed') || el.textContent.includes('Failed')) {
    if (el.children.length === 0 || el.textContent.length < 200) {
      result.push(`[${el.className}] ${el.textContent.trim()}`);
    }
  }
});
result.length ? result.join('\n---\n') : 'No error messages found on page';

// Make the POST and follow redirects, capture flash cookie/session
const csrfToken = document.querySelector('meta[name="csrf-token"]')?.content;
const formData = new FormData();
formData.append('authenticity_token', csrfToken);

fetch('/sandboxes/1/terminal', {
  method: 'POST',
  body: formData,
  redirect: 'follow',
  credentials: 'same-origin'
}).then(async r => {
  const body = await r.text();
  // Look for flash messages in the rendered HTML
  const parser = new DOMParser();
  const doc = parser.parseFromString(body, 'text/html');

  // Check for any alert/notice in the body
  const allText = doc.body?.innerText?.substring(0, 1000) || 'no body';

  return JSON.stringify({
    finalUrl: r.url,
    status: r.status,
    redirected: r.redirected,
    cookies: document.cookie,
    bodyPreview: allText
  }, null, 2);
}).catch(e => `Error: ${e.message}`);

// Try the API to check sandbox status and also see if we can get error details
// First check if there's an API token in cookies or if we can use session auth
fetch('/api/sandboxes/1', {
  credentials: 'same-origin',
  headers: { 'Accept': 'application/json' }
}).then(r => r.text()).then(t => t.substring(0, 500));

