input.command:
cat > /tmp/replace-banner.sed << 'EOF'
/cat > "\$profile_d" <<'BANNER_SCRIPT'/,/^BANNER_SCRIPT$/{
  /cat > "\$profile_d" <<'BANNER_SCRIPT'/a\
@@TEMPLATE:banner.sh@@
  /cat > "\$profile_d" <<'BANNER_SCRIPT'/!{
    /^BANNER_SCRIPT$/!d
  }
}
EOF
sed -i.bak -f /tmp/replace-banner.sed installer/installer.sh.in
diff installer.sh installer/installer.sh.in | head -20 || true

cat > /tmp/replace-compose.sed << 'EOF'
/cat > "\$SANDCASTLE_HOME\/docker-compose\.yml" <<COMPOSE/,/^COMPOSE$/{
  /cat > "\$SANDCASTLE_HOME\/docker-compose\.yml" <<COMPOSE/a\
@@TEMPLATE:docker-compose.yml.template@@
  /cat > "\$SANDCASTLE_HOME\/docker-compose\.yml" <<COMPOSE/!{
    /^COMPOSE$/!d
  }
}
EOF
sed -i.bak2 -f /tmp/replace-compose.sed installer/installer.sh.in
echo "✓ Replaced docker-compose.yml heredoc"

cat > /tmp/replace-postgres.sed << 'EOF'
/cat > "\$SANDCASTLE_HOME\/etc\/postgres\/init-databases\.sh" <<'INITDB'/,/^INITDB$/{
  /cat > "\$SANDCASTLE_HOME\/etc\/postgres\/init-databases\.sh" <<'INITDB'/a\
@@TEMPLATE:/docker/postgres/init-databases.sh@@
  /cat > "\$SANDCASTLE_HOME\/etc\/postgres\/init-databases\.sh" <<'INITDB'/!{
    /^INITDB$/!d
  }
}
EOF
sed -i.bak3 -f /tmp/replace-postgres.sed installer/installer.sh.in
echo "✓ Replaced postgres init script heredoc"

input.old_string:
# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m' # No Color

info() { echo -e "${GREEN}→${NC} $*"; }
warn() { echo -e "${YELLOW}⚠${NC} $*"; }
error() { echo -e "${RED}✗${NC} $*"; }
ok() { echo -e "${GREEN}✓${NC} $*"; }

